import { Button } from "react-bootstrap";

export const PaymentsFilter = students => {
  const { handleSubmit, register, setValue } = useForm();

  const onSubmit = values => {};

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="row formFilter ">
      <div className="buttonSubmit col-sm-12 col-md-3 col-lg-3 col-xl-2">
        <Button
          type="submit"
          className="col-sm-12 col-lg-8 submitButtonFilter btn-text"
        >
          <div className="form-group">
            <label htmlFor="team">{gettext("Equipo")}</label>
            <select
              className="form-control"
              name="team"
              multiple={false}
              ref={this.inTeam}
              value={this.state.card.team}
              onChange={this.handleInputChange}
            >
              {this.state.teams.map(team => (
                <option value={team.id} key={team.id}>
                  {team.name}
                </option>
              ))}
            </select>
            <MessageError field="team" errors={this.state.errors} />
          </div>
          Apply Filter
        </Button>
      </div>
    </form>
  );
};
