import Link from "next/link";
import Head from "next/head";
import Router from "next/router";
import NProgress from "nprogress";
import Auth from "../lib/Auth";
import { useState, useEffect } from "react";

export function Layout({ title, children }) {
  const auth = new Auth();
  const [isLogged, setIsLogged] = useState(false);

  useEffect(() => {
    var user_data = localStorage.getItem("user_details");
    var isLoggedIn = localStorage.getItem("isLoggedIn");

    if (!isLoggedIn || !user_data) {
      setIsLogged(false);
      window.location.replace("/");
    } else {
      setIsLogged(true);
    }
  });
  console.log("here");
  return (
    <div className="root">
      <Head>
        <title>Acamica Students</title>
      </Head>
      <header>
        <Link href="/">
          <a>Home</a>
        </Link>
        {isLogged && (
          <Link href="/payments">
            <a>Payments Information</a>
          </Link>
        )}
        {isLogged && (
          <li className="list">
            <a className="logout" onClick={() => auth.logout()}>
              Logout
            </a>
          </li>
        )}

        {!isLogged && (
          <li className="list">
            <a className="login" onClick={() => auth.login()}>
              Login / Register
            </a>
          </li>
        )}
      </header>

      <h1>{title}</h1>
      {children}

      <footer>Acamica &copy; {new Date().getFullYear()}</footer>
      <style jsx>{`
        .root {
          display: flex;
          justify-content: center;
          align-items: center;
          flex-direction: column;
        }
        header {
          width: 100%;
          display: flex;
          justify-content: space-around;
          padding: 1em;
          font-size: 1.2rem;
          background: #005782;
        }
        header a {
          color: darkgrey;
          text-decoration: none;
        }
        header a:hover {
          font-weight: bold;
          color: lightgrey;
        }
        .login,
        .logout {
          text-decoration: none;
        }
        footer {
          padding: 1em;
        }
        .list {
          list-style: none;
        }
      `}</style>
      <style global jsx>{`
        body {
          margin: 0;
          font-size: 110%;
          background: #f0f0f0;
        }
      `}</style>
    </div>
  );
}
