import { Layout } from "../components/Layout";
import axios from "axios";
import { useState } from "react";
import Table from "react-bootstrap/Table";
import Pagination from "react-bootstrap/Pagination";
import { PaymentsFilter } from "../components/payments/Filter";

const Payments = props => {
  const students = props.students;
  const [studentsFilter, setstudentsFilter] = useState(props.students);

  /* This is for build Pagination of table */
  /* Set the actual page */
  const [page, setpage] = useState(1);

  /* Students per Page */
  let studentsPage = 5;

  /* Numbers of Page */
  let numberPages = students.length / studentsPage;
  console.log(numberPages);

  let items = [];

  for (let number = 1; number <= numberPages; number++) {
    items.push(
      <Pagination.Item key={number} active={number === page}>
        {number}
      </Pagination.Item>
    );
  }

  const paginationBasic = (
    <Pagination>
      <Pagination.First />
      <Pagination.Prev />
      {items}
      <Pagination.Next />
      <Pagination.Last />
    </Pagination>
  );

  return (
    <Layout title="Payments of Students">
      <PaymentsFilter
        students={students}
        careers={props.careers}
        countries={props.countries}
        setstudents={setstudentsFilter}
      />
      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>Name</th>
            <th>Type Payment</th>
            <th>Dues</th>
            <th>Email</th>
            <th>Career</th>
            <th>Country</th>
            <th>City</th>
          </tr>
        </thead>
        <tbody></tbody>
      </Table>
      {paginationBasic}
    </Layout>
  );
};

Payments.getInitialProps = async function() {
  const res = await axios.get("http://0.0.0.0:8000/api/payments/");
  const resCareer = await axios.get(
    "http://0.0.0.0:8000/api/students/careers/"
  );
  const resCountry = await axios.get(
    "http://0.0.0.0:8000/api/students/countries/"
  );
  const students = await res.data;
  const careers = await resCareer.data;
  const countries = await resCountry.data;
  return { students, careers, countries };
};

export default Payments;
