import { useRouter } from "next/router";
import { Layout } from "../../components/Layout";
import { isLoggedIn } from "../../lib/isLoggedIn";
import Error from "next/error";
import { studentPayment } from "../../lib/api";

export default function Payment() {
  const router = useRouter();

  if (router.query.id) {
    console.log(studentPayment(router.query.id));
  }

  return (
    <React.Fragment>
      {isLoggedIn() && router.query.id ? (
        <Layout title="Payment">
          <h1>{router.query.id}</h1>

          <form onSubmit={handleSubmit(onSubmit)} className="row formFilter">
            {/* Name */}
            <div className="form-group  col-md-5">
              <label htmlFor="name">Name</label>
              <input
                name="name"
                className="form-control"
                type="text"
                ref={register}
              />
            </div>

            <div className="form-group  col-md-5">
              <label htmlFor="name">Email</label>
              <input
                name="email"
                className="form-control"
                type="text"
                ref={register}
              />
            </div>
            <div className="form-group">
              <label htmlFor="career">Career</label>
              <select
                className="form-control"
                name="career"
                multiple={false}
                ref={register}
              >
                <option value={"All"} key={-99}>
                  All
                </option>
                {props.careers.map(career => (
                  <option value={career.description} key={career.id}>
                    {career.description}
                  </option>
                ))}
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="country">Country</label>
              <select
                className="form-control"
                name="country"
                multiple={false}
                ref={register}
              >
                <option value={"All"} key={-99}>
                  All
                </option>
                {props.countries.map(country => (
                  <option value={country.description} key={country.id}>
                    {country.description}
                  </option>
                ))}
              </select>
            </div>
          </form>
        </Layout>
      ) : (
        <Error statusCode={403} />
      )}
    </React.Fragment>
  );
}
