import { Layout } from "../components/Layout";
import axios from "axios";
import { useState } from "react";
import Table from "react-bootstrap/Table";
import { PaymentsFilter } from "../components/payments/Filter";
import { Button } from "react-bootstrap";
import Link from "next/link";
import { PaginationTable } from "../components/Pagination";

const Payments = props => {
  const students = props.students;
  const [studentsFilter, setstudentsFilter] = useState(props.students);
  const [pageActual, setpageActual] = useState(1);
  const sizePage = 1;

  const studentsShow = studentsFilter.filter(student => {
    return (
      student.id > (pageActual - 1) * sizePage &&
      student.id <= pageActual * sizePage
    );
  });

  const studentsRender = studentsShow.map((student, key) => {
    return (
      <tr key={key}>
        <td className="d-none d-sm-table-cell">{student.name}</td>
        <td className="d-none d-sm-table-cell">{student.paymentDescription}</td>
        <td className="d-none d-sm-table-cell">
          {student.dues_value ? student.dues_value : "-"}
        </td>
        <td className="d-none d-sm-table-cell">{student.email}</td>
        <td className="d-none d-sm-table-cell">{student.career}</td>
        <td className="d-none d-sm-table-cell">{student.country}</td>
        <td className="d-none d-sm-table-cell">{student.city}</td>
        <td className="d-none d-sm-table-cell">
          <Link href="/payment/[id]" as={`/payment/${student.id}`}>
            <Button>Edit Payment</Button>
          </Link>
        </td>
      </tr>
    );
  });

  return (
    <Layout title="Payments of Students">
      <PaymentsFilter
        students={students}
        careers={props.careers}
        countries={props.countries}
        setstudents={setstudentsFilter}
      />
      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>Name</th>
            <th>Type Payment</th>
            <th>Dues</th>
            <th>Email</th>
            <th>Career</th>
            <th>Country</th>
            <th>City</th>
            <th>Edit Payment</th>
          </tr>
        </thead>
        <tbody>{studentsRender}</tbody>
      </Table>
      <PaginationTable
        lengthArray={studentsFilter.length}
        sizePage={sizePage}
        setpageActual={setpageActual}
        pageActual={pageActual}
      />
    </Layout>
  );
};

Payments.getInitialProps = async function(props) {
  console.log(props)
  const res = await axios.get("http://0.0.0.0:8000/api/payments/");
  const resCareer = await axios.get(
    "http://0.0.0.0:8000/api/students/careers/"
  );
  const resCountry = await axios.get(
    "http://0.0.0.0:8000/api/students/countries/"
  );
  const students = await res.data;
  const careers = await resCareer.data;
  const countries = await resCountry.data;
  return { students, careers, countries };
};

export default Payments;
