import axios from "axios";

export const studentPayment = async paymentId => {
  const { data } = await axios.get(
    `http://0.0.0.0:8000/api/payments/${paymentId}`
  );
  const { student } = await axios
    .get(`http://0.0.0.0:8000/api/students/${data.student}`)
    .then(res => {
      const student = res.data;
      console.log(data);
      return {
        name: student.name,
        email: student.email,
        career: student.career_description,
        dateBirth: student.date_birth,
        phone: student.phone_number,
        country: student.country_description,
        city: student.city_description
      };
    });

  return student;
};
