export const studentPayment = async paymentId => {
  const { data } = await axios.get(
    `http://0.0.0.0:8000/api/payments/${paymentId}`
  );
  const { student } = await axios
    .get(`http://0.0.0.0:8000/api/students/${data.student}`)
    .then(res => {
      console.log(res.data);
