import { useForm } from "react-hook-form";
import { Button, Form } from "react-bootstrap";

export const PaymentsFilter = props => {
  const { handleSubmit, register } = useForm();

  const onSubmit = values => {
    let studentsFilter = props.students

    /* Filter by name */
    if (values.name.trim() !== "") {
      studentsFilter = studentsFilter.filter(
        student => student.name == values.name
      );
    }

    if (values.email.trim() !== "") {
        studentsFilter = studentsFilter.filter(
          student => student.email == values.email
        );
      }

    props.setstudents(studentsFilter);
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="row formFilter ">
      {/* Name */}
      <div className="form-group  col-md-5">
        <label htmlFor="name">Name</label>
        <input
          name="name"
          className="form-control"
          type="text"
          ref={register}
        />
      </div>

      <div className="form-group  col-md-5">
        <label htmlFor="name">Email</label>
        <input
          name="email"
          className="form-control"
          type="text"
          ref={register}
        />
      </div>
      <Form.Group controlId="countrySelect">
        <Form.Label>Country</Form.Label>
        <Form.Control name="country" ref={register} as="select">
          <option>1</option>
          <option>2</option>
          <option>3</option>
          <option>4</option>
          <option>5</option>
        </Form.Control>
      </Form.Group>

      <Form.Group controlId="careerSelect">
        <Form.Label>Career</Form.Label>
        <Form.Control name="career" ref={register} as="select">
          <option>1</option>
          <option>2</option>
          <option>3</option>
          <option>4</option>
          <option>5</option>
        </Form.Control>
      </Form.Group>
      <Button
        type="submit"
        className="col-sm-12 col-lg-8 submitButtonFilter btn-text"
      >
        Apply Filter
      </Button>
    </form>
  );
};
